mapper.update:
update member
set zipcode = #{zipcode}, address = #{address}, 
address_detail = #{address_detail}, hp1 = #{hp1}, hp2 = #{hp2}, 
hp3 = #{hp3}, email1 = #{email1}, email2 = #{email2}
where user_id=#{user_id}

update member
set del_flag = 1, deldate = sysdate
where user_id= #{user_id}
