mapper.select:
select * from ( select rownum as RNUM, A.* from ( select * from review_view
order by review_no desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update review
set title=#{title}, content=#{content}, score=#{score}, regdate=sysdate
where review_no=#{reviewNo}
