mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select * from support
		
    )A
)
where RNUM > #{firstRecordIndex} 

and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select * from support
		
    )A
)
where RNUM > #{firstRecordIndex} 

and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update support
set as_flag='Y'
where support_no=#{supportNo}

mapper.select:
select * from support_view
where group_no=#{groupNo}
and step=1
